export default {
  props: {
    current: {
      //当前页码
      type: Number,
      default: 1
    },
    total: {
      // 总的数据量
      type: Number,
      default: 0
    },
    limit: {
      // 页容量，每页显示多少条数据
      type: Number,
      default: 10
    },
    visibleNumber: {
      // 可见页码数
      type: Number,
      default: 10
    }
  },
  computed: {
    totalPage: function totalPage() {
      // 总共页码数
      return Math.ceil(this.total / this.limit);
    },
    visibleMinPage: function visibleMinPage() {
      // 返回可见的起始页码数（也就是可见的最小页码数）
      var visibleMin = this.current - Math.floor(this.visibleNumber / 2);

      if (visibleMin <= 0) {
        visibleMin = 1;
      }

      return visibleMin;
    },
    visibleMaxPage: function visibleMaxPage() {
      // 返回可见的结尾页码数（也就是可见的最大页码数）
      var visibleMax = this.visibleMinPage + this.visibleNumber - 1;

      if (visibleMax >= this.totalPage) {
        visibleMax = this.totalPage;
      }

      return visibleMax;
    },
    number: function number() {
      // 返回需要生成的可见页码数
      var num = [];

      for (var i = this.visibleMinPage; i <= this.visibleMaxPage; i++) {
        num.push(i);
      }

      return num;
    }
  },
  methods: {
    handleClick: function handleClick(newPage) {
      // 处理页码点击事件，抛出事件，事件参数为 newPage
      // $emit()  方法为抛出事件
      this.$emit("pageChange", newPage);
    }
  }
};
